import React, { useState, useEffect } from 'react';
import './login.css';
import { Link } from 'react-router-dom';

// Usamos require.context para importar todas las imágenes de la carpeta 'assets'
const images = require.context('../../assets', false, /\.(png|jpe?g|svg)$/);

function Login() {
  const [showCard, setShowCard] = useState(false);
  const [username, setUsername] = useState('');
  const [showError, setShowError] = useState(false);

  useEffect(() => {
    // Mostrar la tarjeta después de un segundo
    const timer = setTimeout(() => {
      setShowCard(true);
    }, 1000);

    return () => clearTimeout(timer); // Limpiar el temporizador si el componente se desmonta
  }, []);

  const handleLogin = () => {
    if (!username.trim()) {
      setShowError(true);
    } else {
      setShowError(false);
    }
  };

  return (
    <div className="container full-screen">
      <div className="header-image">
        <img 
          src={images('./Login3.png')} 
          alt="FunQuiz Academy Logo" 
          tabIndex={1} 
          aria-label="Logo de FunQuiz Academy"
        />
      </div>

      {showCard && (
        <div className="overlay-card">
          <div className="card-content">
            <h2 tabIndex={2}>Bienvenido a FunQuiz</h2>
            <p tabIndex={3}>Por favor, ingresa tu nombre de usuario:</p>
            <input
              type="text"
              placeholder="Nombre de usuario"
              className="username-input"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              tabIndex={4}
              aria-label="Campo para ingresar nombre de usuario"
            />
            {showError && (
              <p className="error-message" aria-live="assertive" tabIndex={5}>
                Por favor, ingrese un nombre de usuario.
              </p>
            )}
            <button
              type="button"
              className="btn"
              onClick={handleLogin}
              tabIndex={6}
            >
              Ingresar
            </button>
            {username.trim() && (
              <Link to="/moon-phases">
                <button
                  type="button"
                  className="btn link-btn"
                  tabIndex={7}
                >
                  Continuar
                </button>
              </Link>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
