import React, { useState, useEffect } from 'react';
import './login.css';
import { Link } from 'react-router-dom';

// Usamos require.context para importar todas las imágenes de la carpeta 'assets'
const images = require.context('../../assets', false, /\.(png|jpe?g|svg)$/);

function Login() {
  const [showCard, setShowCard] = useState(false);

  useEffect(() => {
    // Mostrar la tarjeta después de un segundo
    const timer = setTimeout(() => {
      setShowCard(true);
    }, 1000);

    return () => clearTimeout(timer); // Limpiar el temporizador si el componente se desmonta
  }, []);

  return (
    <div className="container full-screen">
      <div className="header-image">
        <img src={images('./Login3.png')} alt="FunQuiz Academy Logo" />
      </div>

      {showCard && (
        <div className="overlay-card">
          <div className="card-content">
            <h2>Por favor, ingresa tu nombre de usuario:</h2>
            <input
              type="text"
              placeholder="Nombre de usuario"
              className="username-input"
            />
            <Link to="/moon-phases">
              <button type="button" className="btn">Ingresar</button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
