import React, { useState, useEffect } from 'react';
import './login.css';
import { Link } from 'react-router-dom';

// Usamos require.context para importar todas las imágenes de la carpeta 'assets'
const images = require.context('../../assets', false, /\.(png|jpe?g|svg)$/);

function Login() {
  const [showCard, setShowCard] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Mostrar la tarjeta después de un segundo
    const timer = setTimeout(() => {
      setShowCard(true);
    }, 1000);

    return () => clearTimeout(timer); // Limpiar el temporizador si el componente se desmonta
  }, []);

  const isUsernameValid = username.trim().length > 0;

  return (
    <div className="container full-screen">
      <div className="header-image">
        <img src={images('./Login3.png')} alt="FunQuiz Academy Logo" />
      </div>

      {showCard && (
        <div className="overlay-card">
          <div className="card-content">
            <h2>Bienvenido a FunQuiz</h2>
            <p>Por favor, ingresa un nombre de usuario para poder continuar:</p>
            <input
              type="text"
              placeholder="Nombre de usuario"
              className="username-input"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <Link to={isUsernameValid ? "/moon-phases" : "#"}>
              <button
                type="button"
                className="btn"
                disabled={!isUsernameValid}
              >
                Ingresar
              </button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
